// QuickBase API configuration
const QB_API_URL = 'https://api.quickbase.com/v1';

// Headers for QuickBase API requests
const getHeaders = () => ({
  'QB-Realm-Hostname': window.QUICKBASE_REALM,
  'User-Agent': 'QuestionAnswerPlatform/1.0',
  'Authorization': `QB-USER-TOKEN ${window.QUICKBASE_USER_TOKEN}`,
  'Content-Type': 'application/json'
});

// Table IDs - Replace these with your actual QuickBase table IDs
export const TABLES = {
  QUESTIONS: 'buzzze3pn',
  ANSWERS: 'buzzzhvkj',
  ATTACHMENTS: 'buz3i82er'
};

// Field IDs mapping
export const FIELDS = {
  RECORD_ID: 3,
  QUESTION: 6,
  REQUIRED: 7,
  FORM_ID: 10
};

// API functions
export const api = {
  // Get all active questions for a specific form
  getQuestions: async (formId: string) => {
    const response = await fetch(`${QB_API_URL}/records/query`, {
      method: 'POST',
      headers: getHeaders(),
      body: JSON.stringify({
        from: TABLES.QUESTIONS,
        select: [FIELDS.RECORD_ID, FIELDS.QUESTION, FIELDS.REQUIRED],
        where: `{${FIELDS.FORM_ID}.EX.'${formId}'}`
      })
    });

    if (!response.ok) {
      const error = await response.json();
      throw new Error(error.message || 'Failed to fetch questions');
    }

    return response.json();
  },

  // Get a single question
  getQuestion: async (questionId: string) => {
    const response = await fetch(`${QB_API_URL}/records/query`, {
      method: 'POST',
      headers: getHeaders(),
      body: JSON.stringify({
        from: TABLES.QUESTIONS,
        select: [FIELDS.RECORD_ID, FIELDS.QUESTION, FIELDS.REQUIRED],
        where: `{${FIELDS.RECORD_ID}.EX.'${questionId}'}`
      })
    });

    if (!response.ok) {
      const error = await response.json();
      throw new Error(error.message || 'Failed to fetch question');
    }

    return response.json();
  },

  // Save an answer
  saveAnswer: async (questionId: string, answer: string, userId: string) => {
    const response = await fetch(`${QB_API_URL}/records`, {
      method: 'POST',
      headers: getHeaders(),
      body: JSON.stringify({
        to: TABLES.ANSWERS,
        data: [{
          10: questionId, // This should be the record ID from the question
          6: answer,
          7: userId,
        }]
      })
    });

    if (!response.ok) {
      const error = await response.json();
      throw new Error(error.message || 'Failed to save answer');
    }

    return response.json();
  },

  // Upload an attachment
  uploadAttachment: async (answerId: string, file: File) => {
    // First, create the attachment record
    const createResponse = await fetch(`${QB_API_URL}/records`, {
      method: 'POST',
      headers: getHeaders(),
      body: JSON.stringify({
        to: TABLES.ATTACHMENTS,
        data: [{
          answer_id: answerId,
          file_name: file.name,
          file_type: file.type,
          file_size: file.size
        }]
      })
    });

    if (!createResponse.ok) {
      const error = await createResponse.json();
      throw new Error(error.message || 'Failed to create attachment record');
    }

    const { data: [{ record_id }] } = await createResponse.json();

    // Then, upload the file content
    const fileContent = await file.arrayBuffer();
    const uploadResponse = await fetch(`${QB_API_URL}/records`, {
      method: 'POST',
      headers: getHeaders(),
      body: JSON.stringify({
        to: TABLES.ATTACHMENTS,
        data: [{
          record_id,
          file_content: fileContent
        }]
      })
    });

    if (!uploadResponse.ok) {
      const error = await uploadResponse.json();
      throw new Error(error.message || 'Failed to upload file content');
    }

    return uploadResponse.json();
  },

  // Get user's progress
  getProgress: async (userId: string) => {
    const response = await fetch(`${QB_API_URL}/records/query`, {
      method: 'POST',
      headers: getHeaders(),
      body: JSON.stringify({
        from: TABLES.ANSWERS,
        select: ['question_id', 'status'],
        where: { user_id: userId }
      })
    });

    if (!response.ok) {
      const error = await response.json();
      throw new Error(error.message || 'Failed to fetch progress');
    }

    return response.json();
  }
};
