import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import {
  List,
  ListItem,
  ListItemText,
  ListItemButton,
  Typography,
  Paper,
  CircularProgress,
  Box
} from '@mui/material';
import { api } from '../config/quickbase';

interface Question {
  [key: number]: string; // Using number keys for QuickBase field IDs
}

const QuestionList = () => {
  const { formId } = useParams<{ formId: string }>();
  const [questions, setQuestions] = useState<Question[]>([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchQuestions = async () => {
      if (!formId) {
        console.error('No form ID provided');
        return;
      }

      try {
        const response = await api.getQuestions(formId);
        setQuestions(response.data);
      } catch (error) {
        console.error('Error fetching questions:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchQuestions();
  }, [formId]);

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="200px">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Paper elevation={3} sx={{ p: 2 }}>
      <Typography variant="h5" gutterBottom>
        Questions
      </Typography>
      <List>
        {questions.map((question) => (
          <ListItem key={question[3]} disablePadding>
            <ListItemButton onClick={() => navigate(`/question/${question[3]}`)}>
              <ListItemText
                primary={question[6]} // Question text
                secondary={`Required: ${question[7] === '1' ? 'Yes' : 'No'}`}
              />
            </ListItemButton>
          </ListItem>
        ))}
      </List>
    </Paper>
  );
};

export default QuestionList;
