import React, { useEffect, useState } from 'react';
import { useParams, useNavigate, useSearchParams } from 'react-router-dom';
import {
  Paper,
  Typography,
  TextField,
  Button,
  Box,
  CircularProgress,
  Alert,
  Chip,
  List,
  ListItem,
  ListItemText,
  IconButton
} from '@mui/material';
import { CloudUpload as CloudUploadIcon, Delete as DeleteIcon } from '@mui/icons-material';
import { api, FIELDS } from '../config/quickbase.ts';

interface QuickBaseField {
  value: string | boolean;
}

interface Question {
  [key: number]: QuickBaseField;
}

interface Answer {
  [key: number]: QuickBaseField;
}

interface Attachment {
  record_id: number;
  [key: number]: {
    value: {
      fileName: string;
      data: string;
    };
  };
}

const QuestionForm = () => {
  const { id } = useParams<{ id: string }>();
  const [searchParams] = useSearchParams();
  const userId = searchParams.get('userId');
  const requestId = searchParams.get('requestId');
  const formId = searchParams.get('formId');
  const navigate = useNavigate();
  const [question, setQuestion] = useState<Question | null>(null);
  const [answer, setAnswer] = useState<Answer | null>(null);
  const [answerText, setAnswerText] = useState('');
  const [file, setFile] = useState<File | null>(null);
  const [attachments, setAttachments] = useState<Attachment[]>([]);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      if (!requestId || !formId || !id) {
        setError('Missing required parameters: requestId, formId, or questionId');
        setLoading(false);
        return;
      }

      try {
        // Fetch question
        const questionResponse = await api.getQuestion(id);
        setQuestion(questionResponse.data[0]);

        // Fetch existing answer
        const answerResponse = await api.getAnswer(id, requestId);
        if (answerResponse.data && answerResponse.data.length > 0) {
          setAnswer(answerResponse.data[0]);
          setAnswerText(String(answerResponse.data[0][FIELDS.ANSWERS.ANSWER].value));

          // Fetch attachments for the answer
          const attachmentsResponse = await api.getAttachmentsForAnswer(
            Number(answerResponse.data[0][FIELDS.ANSWERS.RECORD_ID].value)
          );
          setAttachments(attachmentsResponse.data);
        }
      } catch (error) {
        setError('Failed to load data');
        console.error('Error fetching data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [id, requestId, formId]);

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    if (event.target.files && event.target.files[0]) {
      setFile(event.target.files[0]);
    }
  };

  const handleDeleteAttachment = async (recordId: number) => {
    try {
      await api.deleteAttachment(recordId);
      setAttachments(attachments.filter(att => att.record_id !== recordId));
    } catch (error) {
      setError('Failed to delete attachment');
      console.error('Error deleting attachment:', error);
    }
  };

  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();
    if (!userId || !requestId || !formId || !id) {
      setError('Missing required parameters');
      return;
    }

    setSaving(true);
    setError(null);

    try {
      let answerResponse;

      if (answer) {
        // Update existing answer
        answerResponse = await api.updateAnswer(
          String(answer[FIELDS.ANSWERS.RECORD_ID].value),
          answerText,
          userId,
          requestId
        );
      } else {
        // Create new answer
        answerResponse = await api.saveAnswer(
          id,
          answerText,
          userId,
          requestId
        );
      }
      console.log(answerResponse);
      // Upload file if selected
      if (file) {
        await api.uploadFileAttachment(
          Number(answerResponse.data[0].record_id),
          file.name,
          file
        );
      }

      setSuccess(true);
      setTimeout(() => navigate(`/?userId=${userId}&requestId=${requestId}&formId=${formId}`), 2000);
    } catch (error) {
      setError('Failed to save answer');
      console.error('Error saving answer:', error);
    } finally {
      setSaving(false);
    }
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="200px">
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Alert severity="error" sx={{ mb: 2 }}>
        {error}
      </Alert>
    );
  }

  if (!question) {
    return (
      <Alert severity="error">
        Question not found
      </Alert>
    );
  }

  return (
    <Paper elevation={3} sx={{ p: 3 }}>
      <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
        <Typography variant="h5">
          {String(question[FIELDS.QUESTIONS.QUESTION].value)}
        </Typography>
        {answer && (
          <Chip
            label="Previously Answered"
            color="success"
            variant="outlined"
          />
        )}
      </Box>
      <form onSubmit={handleSubmit}>
        <TextField
          fullWidth
          multiline
          rows={4}
          variant="outlined"
          label="Your Answer"
          value={answerText}
          onChange={(e) => setAnswerText(e.target.value)}
          margin="normal"
          required={question[FIELDS.QUESTIONS.REQUIRED].value as boolean}
        />
        <Box sx={{ mt: 2, mb: 2 }}>
          <input
            accept="image/*"
            style={{ display: 'none' }}
            id="file-upload"
            type="file"
            onChange={handleFileChange}
          />
          <label htmlFor="file-upload">
            <Button
              variant="outlined"
              component="span"
              startIcon={<CloudUploadIcon />}
            >
              Upload Attachment
            </Button>
          </label>
          {file && (
            <Typography variant="body2" sx={{ mt: 1 }}>
              Selected file: {file.name}
            </Typography>
          )}
        </Box>

        {attachments.length > 0 && (
          <Box sx={{ mt: 2 }}>
            <Typography variant="subtitle1">Attachments:</Typography>
            <List>
              {attachments.map((attachment) => (
                <ListItem
                  key={attachment.record_id}
                  secondaryAction={
                    <IconButton edge="end" onClick={() => handleDeleteAttachment(attachment.record_id)}>
                      <DeleteIcon />
                    </IconButton>
                  }
                >
                  <ListItemText
                    primary={attachment[FIELDS.ATTACHMENTS.FILE_ATTACHMENT].value.fileName}
                  />
                </ListItem>
              ))}
            </List>
          </Box>
        )}

        {error && (
          <Alert severity="error" sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}
        {success && (
          <Alert severity="success" sx={{ mb: 2 }}>
            Answer saved successfully!
          </Alert>
        )}
        <Button
          type="submit"
          variant="contained"
          color="primary"
          disabled={saving}
        >
          {saving ? <CircularProgress size={24} /> : answer ? 'Update Answer' : 'Save Answer'}
        </Button>
      </form>
    </Paper>
  );
};

export default QuestionForm;
