import React, { useEffect, useState } from 'react';
import { useParams, useNavigate, useSearchParams } from 'react-router-dom';
import {
  Paper,
  Typography,
  TextField,
  Button,
  Box,
  CircularProgress,
  Alert
} from '@mui/material';
import { CloudUpload as CloudUploadIcon } from '@mui/icons-material';
import { api } from '../config/quickbase.ts';

interface QuickBaseField {
  value: string | boolean;
}

interface Question {
  [key: number]: QuickBaseField;
}

const QuestionForm = () => {
  const { id } = useParams<{ id: string }>();
  const [searchParams] = useSearchParams();
  const userId = searchParams.get('userId');
  const requestId = searchParams.get('requestId');
  const formId = searchParams.get('formId');
  const navigate = useNavigate();
  const [question, setQuestion] = useState<Question | null>(null);
  const [answer, setAnswer] = useState('');
  const [file, setFile] = useState<File | null>(null);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState(false);

  useEffect(() => {
    const fetchQuestion = async () => {
      if (!userId || !requestId || !formId || !id) {
        setError('Missing required parameters: userId, requestId, formId, or questionId');
        setLoading(false);
        return;
      }

      try {
        const response = await api.getQuestion(id);
        setQuestion(response.data[0]);
      } catch (error) {
        setError('Failed to load question');
        console.error('Error fetching question:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchQuestion();
  }, [id, userId, requestId, formId]);

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    if (event.target.files && event.target.files[0]) {
      setFile(event.target.files[0]);
    }
  };

  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();
    if (!userId || !requestId || !formId || !id) {
      setError('Missing required parameters');
      return;
    }

    setSaving(true);
    setError(null);

    try {
      // Save answer
      const answerResponse = await api.saveAnswer(
        id,
        answer,
        userId,
        requestId
      );

      // Upload file if selected
      if (file) {
        await api.uploadAttachment(answerResponse.data[0].record_id, file);
      }

      setSuccess(true);
      setTimeout(() => navigate(`/form/${formId}?userId=${userId}&requestId=${requestId}&formId=${formId}`), 2000);
    } catch (error) {
      setError('Failed to save answer');
      console.error('Error saving answer:', error);
    } finally {
      setSaving(false);
    }
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="200px">
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Alert severity="error" sx={{ mb: 2 }}>
        {error}
      </Alert>
    );
  }

  if (!question) {
    return (
      <Alert severity="error">
        Question not found
      </Alert>
    );
  }

  return (
    <Paper elevation={3} sx={{ p: 3 }}>
      <Typography variant="h5" gutterBottom>
        {String(question[6].value)}
      </Typography>
      <form onSubmit={handleSubmit}>
        <TextField
          fullWidth
          multiline
          rows={4}
          variant="outlined"
          label="Your Answer"
          value={answer}
          onChange={(e) => setAnswer(e.target.value)}
          margin="normal"
          required={question[7].value as boolean}
        />
        <Box sx={{ mt: 2, mb: 2 }}>
          <input
            accept="image/*"
            style={{ display: 'none' }}
            id="file-upload"
            type="file"
            onChange={handleFileChange}
          />
          <label htmlFor="file-upload">
            <Button
              variant="outlined"
              component="span"
              startIcon={<CloudUploadIcon />}
            >
              Upload Attachment
            </Button>
          </label>
          {file && (
            <Typography variant="body2" sx={{ mt: 1 }}>
              Selected file: {file.name}
            </Typography>
          )}
        </Box>
        {error && (
          <Alert severity="error" sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}
        {success && (
          <Alert severity="success" sx={{ mb: 2 }}>
            Answer saved successfully!
          </Alert>
        )}
        <Button
          type="submit"
          variant="contained"
          color="primary"
          disabled={saving}
        >
          {saving ? <CircularProgress size={24} /> : 'Save Answer'}
        </Button>
      </form>
    </Paper>
  );
};

export default QuestionForm;
